div:not(.injected) h1 {
  font-size: 54px !important;
  color: white !important;
  font-family: 'Montserrat', sans-serif;
}
.simple-message-app {
  width: 100%;
  max-width: 960px;
  margin: 100px auto 0;
}
.message-list {
  list-style-type: none;
  padding: 0;
}
.message-list .message-item {
  border-width: 1px 1px 0 1px;
  border-style: solid;
  border-color: #000;
  padding: 25px;
  background-color: white;
  display: flex;
  align-items: flex-start;
}
.message-list .message-item:last-child {
  border-bottom-width: 1px;
}
.message-list .message-item .message-name {
  flex: 1;
  font-size: 25px;
}
.message-list .message-item .message-subjects {
  flex: 3;
}
.message-list .message-item .message-subject {
  display: block;
  margin-bottom: 15px;
}
.message-list .message-item .message-subject:last-child {
  margin: 0;
}
.message-list .message-item .message-action {
  flex: 1;
  background: #ccc;
  border-radius: 3px;
  font-size: 12px;
  padding: 10px;
  font-family: 'Montserrat', sans-serif;
  border: none;
  outline: none;
}
.message-preview-container {
  background-color: white;
}
.message-preview-container .message-preview-header {
  padding: 25px;
  border-bottom: 1px solid #ddd;
}
.message-preview-container .message-preview-action {
  background: #ccc;
  border-radius: 3px;
  font-size: 12px;
  padding: 10px;
  font-family: 'Montserrat', sans-serif;
  border: none;
  outline: none;
}
.message-preview-container .message-preview-action.left {
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
  border-right: 1px solid #888;
}
.message-preview-container .message-preview-action.right {
  border-top-left-radius: 0;
  border-bottom-left-radius: 0;
}
.message-preview-container .message-preview-action--active {
  background-color: #aaa;
}
.message-preview-container .message-preview-close {
  border-bottom: 1px solid #aaa;
  padding: 10px;
  text-align: right;
}
.message-preview-container .message-preview-label,
.message-preview-container .message-preview-value {
  font-size: 25px;
}
.message-preview-container .message-preview-value {
  margin-left: 12.5px;
}
.message-preview-container .message-preview-value {
  font-weight: 300;
}
.message-preview-container .message-preview-row {
  display: flex;
  margin-bottom: 12.5px;
}
.message-preview-container .message-preview-row:last-of-type {
  margin-bottom: 25px;
}

